mapper.select:
SELECT * FROM gift_config
WHERE gift_id = #{giftId}
  AND status = #{status}
LIMIT 1

mapper.update:
UPDATE gift_config
SET price = #{price}, gift_name = #{giftName}, status = #{status}, cover_img_url = #{coverImgUrl}, svga_url = #{svgaUrl}, update_time = #{updateTime}
WHERE gift_id = #{giftId}
